pw.test("Status code is 200 and response body matches", () => {
  const expectedFirstName = "John"
  const expectedLastName = "Doe"

  pw.expect(pw.response.status).toBe(200)

  const { firstName, lastName } = JSON.parse(pw.response.body.data)

  pw.expect(expectedFirstName).toBe(firstName)
  pw.expect(expectedLastName).toBe(lastName)
});
